const getLibraryIcon = ({ theme }: { theme: string }) =>
  `data:image/svg+xml;base64,${utf8ToB64(libraryIconSvg(theme))}`;

function utf8ToB64(str: string) {
  return window.btoa(unescape(encodeURIComponent(str)));
}
